let url="http://localhost:3000/api/products";fetch(url).then(response=>response.json().then(lesCanaps=>{console.log(lesCanaps);for(const unCanap of lesCanaps){let articleHTML=`\n            <a href="./product.html?id=${unCanap._id}">\n                <article>\n                    <img src="${unCanap.imageUrl}" alt="${unCanap.altTxt}">\n                    <h3 class="productName">${unCanap.name}</h3>\n                    <p class="productDescription">${unCanap.description}</p>\n                </article>\n            </a>`;document.getElementById("items").innerHTML+=articleHTML}})).catch(erreur=>{document.getElementById("items").innerHTML='<p class="erreur">Serveur inaccessible, recommencer plus tard ...</p>'});